

"""dnspython release version information."""

MAJOR = 2
MINOR = 8
MICRO = 0
RELEASELEVEL = 0x0F
SERIAL = 0

if RELEASELEVEL == 0x0F:
    version = f"{MAJOR}.{MINOR}.{MICRO}"
elif RELEASELEVEL == 0x00:
    version = f"{MAJOR}.{MINOR}.{MICRO}dev{SERIAL}"
elif RELEASELEVEL == 0x0C:
    version = f"{MAJOR}.{MINOR}.{MICRO}rc{SERIAL}"
else:
    version = f"{MAJOR}.{MINOR}.{MICRO}{RELEASELEVEL:x}{SERIAL}"

hexversion = MAJOR << 24 | MINOR << 16 | MICRO << 8 | RELEASELEVEL << 4 | SERIAL